          </Typography>
          {/* ÎèÑÏö∞ÎØ∏ ÏïàÎÇ¥Ìëú */}
          <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
            {helpers.map((helper, idx) => {
              const emojis = ['üë©‚Äçüëß', 'üëµ', 'üë®‚Äçüëß', 'üë©‚Äçü¶∞', 'üëµ'];
