import React from 'react';
import { Paper, Box, Typography, Button, Tabs, Tab, Chip } from '@mui/material';
import { DateCalendar } from '@mui/x-date-pickers/DateCalendar';
import { Schedule } from '../types';
import { format, getDay, differenceInMinutes } from 'date-fns';

interface CalendarProps {
  events: Schedule[];
  onDeleteEvent: (eventId: string) => void;
}

const SLOT_HEIGHT = 48; // 1시간 슬롯의 높이(px)

const Calendar: React.FC<CalendarProps> = ({ events, onDeleteEvent }) => {
  const [selectedDate, setSelectedDate] = React.useState<Date | null>(new Date());
  const [selectedPerson, setSelectedPerson] = React.useState<'조운' | '지윤' | '전체'>('전체');

  const getEventsForDate = (date: Date) => {
    const dayOfWeek = getDay(date); // 0: 일요일, 1: 월요일, ..., 6: 토요일
    
    return events.filter(event => {
      // 해당 날짜가 이벤트 기간 내에 있는지 확인
      const isInPeriod = date >= event.start && date <= event.end;
      // 해당 요일에 수업이 있는지 확인
      const isOnDay = event.dayOfWeek.includes(dayOfWeek);
      // 선택된 사람의 일정인지 확인
      const isSelectedPerson = selectedPerson === '전체' || event.person === selectedPerson;
      
      return isInPeriod && isOnDay && isSelectedPerson;
    }).sort((a, b) => {
      const timeA = a.timeSlot.split('-')[0];
      const timeB = b.timeSlot.split('-')[0];
      return timeA.localeCompare(timeB);
    });
  };

  // 오전 8시부터 저녁 9시까지 1시간 단위로 시간대 생성
  const timeSlots = Array.from({ length: 14 }, (_, i) => 
    `${(i + 8).toString().padStart(2, '0')}:00`
  );

  const filteredEvents = selectedDate ? getEventsForDate(selectedDate) : [];

  // 시간 문자열(HH:MM)을 Date 객체로 변환 (선택한 날짜 기준)
  const getSlotDate = (baseDate: Date, time: string) => {
    const [hour, minute] = time.split(':');
    const d = new Date(baseDate);
    d.setHours(Number(hour), Number(minute), 0, 0);
    return d;
  };

  // 이 슬롯에 걸쳐 있는 일정(조운/지윤)을 각각 반환
  const getEventsInSlotByPerson = (slotTime: string) => {
    const slot = getSlotDate(selectedDate!, slotTime);
    const nextSlot = getSlotDate(selectedDate!, `${(parseInt(slotTime.split(':')[0]) + 1).toString().padStart(2, '0')}:00`);
    const result: { [person: string]: { event: Schedule, isStart: boolean, isEnd: boolean } | null } = { '조운': null, '지윤': null };
    filteredEvents.forEach(event => {
      const [startStr, endStr] = event.timeSlot.split('-');
      const start = getSlotDate(selectedDate!, startStr);
      const end = getSlotDate(selectedDate!, endStr);
      if (slot < end && nextSlot > start) {
        const isStart = slot.getTime() === start.getTime();
        const isEnd = nextSlot >= end;
        result[event.person] = { event, isStart, isEnd };
      }
    });
    return result;
  };

  // 일정의 전체 길이만큼 박스 높이 계산 (단, 전체탭에서는 한 슬롯=1칸)
  const getEventBoxHeight = (event: Schedule, slotTime: string, isEnd: boolean) => {
    const [startStr, endStr] = event.timeSlot.split('-');
    const slot = getSlotDate(selectedDate!, slotTime);
    const nextSlot = getSlotDate(selectedDate!, `${(parseInt(slotTime.split(':')[0]) + 1).toString().padStart(2, '0')}:00`);
    const start = getSlotDate(selectedDate!, startStr);
    const end = getSlotDate(selectedDate!, endStr);
    // 이 슬롯이 마지막 슬롯이면 남은 시간만큼, 아니면 1칸
    if (isEnd) {
      const minutes = differenceInMinutes(end, slot);
      return (minutes / 60) * SLOT_HEIGHT;
    }
    return SLOT_HEIGHT;
  };

  // 이 슬롯이 일정의 시작/중간/끝인지 판단하여 borderRadius 스타일 반환
  const getSlotBoxRadius = (isStart: boolean, isEnd: boolean) => {
    if (isStart && isEnd) return '12px'; // 한 슬롯만 차지
    if (isStart) return '12px 12px 0 0'; // 위만 둥글게
    if (isEnd) return '0 0 12px 12px'; // 아래만 둥글게
    return '0'; // 중간
  };

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 2 }}>
      <Box sx={{ display: 'flex', gap: 2 }}>
        <DateCalendar
          value={selectedDate}
          onChange={(newDate) => setSelectedDate(newDate)}
        />
        <Box sx={{ flex: 1, p: 2 }}>
          <Typography variant="h6" gutterBottom>
            {selectedDate ? format(selectedDate, 'yyyy년 MM월 dd일') : '날짜를 선택하세요'}
          </Typography>
          <Tabs
            value={selectedPerson}
            onChange={(_, newValue) => setSelectedPerson(newValue)}
            sx={{ mb: 2 }}
          >
            <Tab label="전체" value="전체" />
            <Tab label="조운" value="조운" />
            <Tab label="지윤" value="지윤" />
          </Tabs>
          {selectedDate && (
            <Box>
              {timeSlots.map((timeSlot) => {
                if (selectedPerson === '전체') {
                  const eventsByPerson = getEventsInSlotByPerson(timeSlot);
                  return (
                    <Box key={timeSlot} sx={{ mb: 0, display: 'flex', alignItems: 'flex-start' }}>
                      <Typography variant="subtitle2" color="text.secondary" gutterBottom sx={{ width: 60, minWidth: 60, textAlign: 'right', pt: '12px' }}>
                        {timeSlot}
                      </Typography>
                      <Box sx={{ flex: 1, ml: 2, display: 'flex', gap: 2 }}>
                        {['조운', '지윤'].map(person => {
                          const info = eventsByPerson[person];
                          if (info) {
                            const { event, isStart, isEnd } = info;
                            return (
                              <Paper
                                key={event.id + '-' + timeSlot}
                                sx={{
                                  p: isStart ? 1 : 0,
                                  mb: 1,
                                  bgcolor: person === '조운' ? 'primary.light' : 'secondary.light',
                                  display: 'flex',
                                  flexDirection: 'column',
                                  justifyContent: 'space-between',
                                  alignItems: 'flex-start',
                                  height: `${getEventBoxHeight(event, timeSlot, isEnd)}px`,
                                  minHeight: '24px',
                                  width: '50%',
                                  borderRadius: getSlotBoxRadius(isStart, isEnd),
                                  boxShadow: isStart ? undefined : 'none',
                                }}
                              >
                                {isStart && (
                                  <>
                                    <Box>
                                      <Box sx={{ display: 'flex', gap: 1, alignItems: 'center', mb: 0.5 }}>
                                        <Typography variant="subtitle1">{event.title}</Typography>
                                        <Chip
                                          label={person}
                                          size="small"
                                          color={person === '조운' ? 'primary' : 'secondary'}
                                        />
                                      </Box>
                                      <Typography variant="body2">{event.location}</Typography>
                                      <Typography variant="body2">{event.timeSlot}</Typography>
                                    </Box>
                                    <Button
                                      size="small"
                                      color="error"
                                      onClick={() => onDeleteEvent(event.id)}
                                    >
                                      삭제
                                    </Button>
                                  </>
                                )}
                              </Paper>
                            );
                          } else {
                            return <Box key={person + '-' + timeSlot} sx={{ width: '50%', height: `${SLOT_HEIGHT}px` }} />;
                          }
                        })}
                      </Box>
                    </Box>
                  );
                } else {
                  // 기존 단일인원 렌더링
                  const eventsStartingHere = filteredEvents.filter(event => {
                    const [eventStartStr] = event.timeSlot.split('-');
                    return eventStartStr === timeSlot;
                  });
                  return (
                    <Box key={timeSlot} sx={{ mb: 0, display: 'flex', alignItems: 'flex-start' }}>
                      <Typography variant="subtitle2" color="text.secondary" gutterBottom sx={{ width: 60, minWidth: 60, textAlign: 'right', pt: '12px' }}>
                        {timeSlot}
                      </Typography>
                      <Box sx={{ flex: 1, ml: 2, position: 'relative' }}>
                        {eventsStartingHere.length > 0 ? (
                          eventsStartingHere.map(event => (
                            <Paper
                              key={event.id + '-' + timeSlot}
                              sx={{
                                p: 1,
                                mb: 1,
                                bgcolor: event.person === '조운' ? 'primary.light' : 'secondary.light',
                                display: 'flex',
                                justifyContent: 'space-between',
                                alignItems: 'flex-start',
                                height: `${getEventBoxHeight(event, timeSlot, true)}px`,
                                minHeight: '24px',
                                position: 'absolute',
                                top: 0,
                                left: 0,
                                right: 0,
                                borderRadius: '12px',
                              }}
                            >
                              <Box>
                                <Box sx={{ display: 'flex', gap: 1, alignItems: 'center', mb: 0.5 }}>
                                  <Typography variant="subtitle1">{event.title}</Typography>
                                  <Chip
                                    label={event.person}
                                    size="small"
                                    color={event.person === '조운' ? 'primary' : 'secondary'}
                                  />
                                </Box>
                                <Typography variant="body2">{event.location}</Typography>
                                <Typography variant="body2">{event.timeSlot}</Typography>
                              </Box>
                              <Button
                                size="small"
                                color="error"
                                onClick={() => onDeleteEvent(event.id)}
                              >
                                삭제
                              </Button>
                            </Paper>
                          ))
                        ) : (
                          <Box sx={{ height: `${SLOT_HEIGHT}px` }} />
                        )}
                      </Box>
                    </Box>
                  );
                }
              })}
              {filteredEvents.length === 0 && (
                <Typography variant="body1" color="text.secondary">
                  예정된 일정이 없습니다.
                </Typography>
              )}
            </Box>
          )}
        </Box>
      </Box>
    </Paper>
  );
};

export default Calendar;
