import React, { useState } from 'react';
import {
  Paper,
  TextField,
  Button,
  Box,
  Typography,
  Grid,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  OutlinedInput,
} from '@mui/material';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import { Schedule } from '../types';

interface EventFormProps {
  onAddEvent: (event: Schedule) => void;
}

const DAYS_OF_WEEK = [
  { value: 0, label: '일요일' },
  { value: 1, label: '월요일' },
  { value: 2, label: '화요일' },
  { value: 3, label: '수요일' },
  { value: 4, label: '목요일' },
  { value: 5, label: '금요일' },
  { value: 6, label: '토요일' },
];

const EventForm: React.FC<EventFormProps> = ({ onAddEvent }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [person, setPerson] = useState<'조운' | '지윤'>('조운');
  const [start, setStart] = useState<Date | null>(new Date());
  const [end, setEnd] = useState<Date | null>(new Date());
  const [location, setLocation] = useState('');
  const [period, setPeriod] = useState<1 | 2 | 3>(1);
  const [dayOfWeek, setDayOfWeek] = useState<number[]>([]);
  const [timeSlot, setTimeSlot] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (title && description && person && start && end && location && timeSlot && dayOfWeek.length > 0) {
      const newEvent: Schedule = {
        id: Date.now().toString(),
        title,
        description,
        person,
        start,
        end,
        location,
        period,
        dayOfWeek,
        timeSlot,
      };
      onAddEvent(newEvent);
      setTitle('');
      setDescription('');
      setPerson('조운');
      setStart(new Date());
      setEnd(new Date());
      setLocation('');
      setPeriod(1);
      setDayOfWeek([]);
      setTimeSlot('');
    }
  };

  return (
    <Paper elevation={3} sx={{ p: 2 }}>
      <Typography variant="h6" gutterBottom>
        새로운 일정 추가
      </Typography>
      <Box component="form" onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="제목"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="설명"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              multiline
              rows={2}
            />
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="장소"
              value={location}
              onChange={(e) => setLocation(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>이름</InputLabel>
              <Select
                value={person}
                label="이름"
                onChange={(e) => setPerson(e.target.value as '조운' | '지윤')}
                required
              >
                <MenuItem value="조운">조운</MenuItem>
                <MenuItem value="지윤">지윤</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>구간</InputLabel>
              <Select
                value={period}
                label="구간"
                onChange={(e) => setPeriod(e.target.value as 1 | 2 | 3)}
                required
              >
                <MenuItem value={1}>구간 1 (6/13 ~ 7/14)</MenuItem>
                <MenuItem value={2}>구간 2 (7/15 ~ 7/27)</MenuItem>
                <MenuItem value={3}>구간 3 (7/28 ~ 8/15)</MenuItem>
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>요일</InputLabel>
              <Select
                multiple
                value={dayOfWeek}
                onChange={(e) => setDayOfWeek(e.target.value as number[])}
                input={<OutlinedInput label="요일" />}
                renderValue={(selected) => (
                  <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                    {selected.map((value) => (
                      <Chip key={value} label={DAYS_OF_WEEK.find(day => day.value === value)?.label} />
                    ))}
                  </Box>
                )}
                required
              >
                {DAYS_OF_WEEK.map((day) => (
                  <MenuItem key={day.value} value={day.value}>
                    {day.label}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="시간 (예: 09:00-10:00)"
              value={timeSlot}
              onChange={(e) => setTimeSlot(e.target.value)}
              required
              placeholder="09:00-10:00"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <DatePicker
              label="시작일"
              value={start}
              onChange={(newValue) => setStart(newValue)}
              sx={{ width: '100%' }}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <DatePicker
              label="종료일"
              value={end}
              onChange={(newValue) => setEnd(newValue)}
              sx={{ width: '100%' }}
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              type="submit"
              variant="contained"
              color="primary"
              fullWidth
            >
              일정 추가
            </Button>
          </Grid>
        </Grid>
      </Box>
    </Paper>
  );
};

export default EventForm;
