import React, { useState, useEffect } from 'react';
import { Container, Box, Typography } from '@mui/material';
import Calendar from './components/Calendar';
import EventForm from './components/EventForm';
import { Schedule } from './types';

const STORAGE_KEY = 'vacation-schedules';

// 초기 스케줄 데이터
const initialSchedules: Schedule[] = [
  // 조운이 구간 1
  {
    id: '1',
    title: '피아노',
    start: new Date('2025-06-13'),
    end: new Date('2025-07-14'),
    description: '피아노 레슨',
    person: '조운',
    location: '피아노 학원',
    period: 1,
    dayOfWeek: [1, 2, 3, 4, 5], // 월~금
    timeSlot: '11:00-12:30'
  },
  {
    id: '2',
    title: '삼성레포츠 수영',
    start: new Date('2025-06-13'),
    end: new Date('2025-07-14'),
    description: '수영 레슨',
    person: '조운',
    location: '삼성레포츠',
    period: 1,
    dayOfWeek: [2, 3, 4, 5], // 화~금
    timeSlot: '09:00-10:00'
  },
  // ... 나머지 스케줄도 같은 형식으로 추가
];

function App() {
  const [schedules, setSchedules] = useState<Schedule[]>(() => {
    const savedSchedules = localStorage.getItem(STORAGE_KEY);
    if (savedSchedules) {
      return JSON.parse(savedSchedules).map((schedule: any) => ({
        ...schedule,
        start: new Date(schedule.start),
        end: new Date(schedule.end)
      }));
    }
    return initialSchedules;
  });

  useEffect(() => {
    localStorage.setItem(STORAGE_KEY, JSON.stringify(schedules));
  }, [schedules]);

  const handleAddEvent = (newEvent: Schedule) => {
    setSchedules([...schedules, newEvent]);
  };

  const handleDeleteEvent = (eventId: string) => {
    setSchedules(schedules.filter(event => event.id !== eventId));
  };

  return (
    <Container maxWidth="lg">
      <Box sx={{ my: 4 }}>
        <Typography variant="h3" component="h1" gutterBottom align="center">
          2025년 여름방학 스케줄
        </Typography>
        <EventForm onAddEvent={handleAddEvent} />
        <Calendar events={schedules} onDeleteEvent={handleDeleteEvent} />
      </Box>
    </Container>
  );
}

export default App;
